package com.lzn.springcloud.service;import com.lzn.springcloud.entities.Dept;import org.springframework.cloud.netflix.feign.FeignClient;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import java.util.List;//@FeignClient(value = "MICROSERVICECLOUD-DEPT") // 没有 hystrix@FeignClient(value = "MICROSERVICECLOUD-DEPT", fallbackFactory = DeptClientServiceFallbackFactory.class)    // 添加 hystrix@RequestMapping("/dept")public interface DeptClientService {    @PostMapping(value = "/get/{id}")    public Dept get(@PathVariable("id") Long id);    @PostMapping("/list")    public List<Dept> list();    @PostMapping("/add")    public boolean add(Dept dept);}