package com.lzn.springcloud.service.impl;import com.lzn.springcloud.dao.DeptDao;import com.lzn.springcloud.entities.Dept;import com.lzn.springcloud.service.IDeptService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class DeptServiceImpl implements IDeptService {    @Autowired    private DeptDao dao;    @Override    public boolean add(Dept dept) {        return dao.addDept(dept);    }    @Override    public Dept get(Long id) {        return dao.findById(id);    }    @Override    public List<Dept> list() {        return dao.findAll();    }}