package com.lzn.springcloud.controller;import com.lzn.springcloud.entities.Dept;import com.lzn.springcloud.service.DeptClientService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/consumerFeign")public class DeptController_ConsumerFeign {    @Autowired    private DeptClientService deptClientService;    @RequestMapping("/dept/add")    public boolean add(Dept dept) {        return deptClientService.add(dept);    }    @RequestMapping("/dept/get/{id}")    public Dept get(@PathVariable Long id) {        return deptClientService.get(id);    }    @RequestMapping("/dept/list")    public List<Dept> list() {        return deptClientService.list();    }    @RequestMapping("/")    public String test() {        return "hehe";    }}